import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Button, Container, Table } from "reactstrap";
import { request } from "../util/APIUtils";
import LeftSideComponent from "./LeftSideComponent";
import { API_BASE_URL, POLL_LIST_SIZE, ACCESS_TOKEN } from "../constants/index";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = { slams: [], isloading: true };
  }

  componentDidMount() {
    this.setState({ isLoading: true });

    request({
      url: API_BASE_URL + "/slams",
      method: "GET"
    }).then(data => this.setState({ slams: data, isLoading: false }));
  }
  render() {
    const { slams, isLoading } = this.state;

    if (isLoading) {
      return <p>Loading...</p>;
    }
    return (
      <div className="App">
        <p className="bg-primary">Welcome!! this is home page</p>

        <Button color="success" tag={Link} to="/createSlam">
          create new slam
        </Button>
        <LeftSideComponent />
        <Container>
          <Table className="mt-4">
            <thead>
              <tr>
                <th>name</th>
                <th>email</th>
                <th />
              </tr>
            </thead>
            <tbody>
              {slams.map(slam => {
                return (
                  <tr key={slam.id}>
                    <td>{slam.name}</td>
                    <td>{slam.emailid}</td>
                    <td>
                      <Button
                        color="success"
                        tag={Link}
                        to={"/slam/" + slam.id}
                      >
                        open slam
                      </Button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </Table>
        </Container>
      </div>
    );
  }
}

export default Home;
