import React, { Component } from "react";
import "./App.css";
import { Route, withRouter, Switch } from "react-router-dom";

import { getCurrentUser, signup } from "../util/APIUtils";
import { ACCESS_TOKEN } from "../constants";

// import PollList from '../poll/PollList';
// import NewPoll from '../poll/NewPoll';
import Login from "../user/login/Login";
import Signup from "../user/signup/Signup";
import Profile from "../user/profile/Profile";
import Home from "../components/home";
import CreateSlam from "../components/create_slam";
import OpenSlam from "../components/open_slam";
import LeftSideComponent from "../components/LeftSideComponent";

import AppHeader from "../common/AppHeader";
// import NotFound from "../common/NotFound";
import LoadingIndicator from "../common/LoadingIndicator";
import PrivateRoute from "../common/PrivateRoute";

import { Layout, notification } from "antd";
const { Content } = Layout;

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentUser: null,
      isAuthenticated: false,
      isLoading: false
    };
    this.handleLogout = this.handleLogout.bind(this);
    this.loadCurrentUser = this.loadCurrentUser.bind(this);
    this.handleLogin = this.handleLogin.bind(this);

    notification.config({
      placement: "topRight",
      top: 70,
      duration: 3
    });
  }

  loadCurrentUser() {
    this.setState({
      isLoading: true
    });
    getCurrentUser()
      .then(response => {
        this.setState({
          currentUser: response,
          isAuthenticated: true,
          isLoading: false
        });
      })
      .catch(error => {
        this.setState({
          isLoading: false
        });
      });
  }

  componentDidMount() {
    this.loadCurrentUser();
  }

  handleLogout(
    redirectTo = "/",
    notificationType = "success",
    description = "You're successfully logged out."
  ) {
    localStorage.removeItem(ACCESS_TOKEN);

    this.setState({
      currentUser: null,
      isAuthenticated: false
    });

    this.props.history.push(redirectTo);

    notification[notificationType]({
      message: "Polling App",
      description: description
    });
  }

  handleLogin() {
    notification.success({
      message: "Polling App",
      description: "You're successfully logged in."
    });
    this.loadCurrentUser();
    this.props.history.push("/home");
  }

  render() {
    if (this.state.isLoading) {
      return <LoadingIndicator />;
    }
    return (
      <Layout className="app-container">
        <AppHeader
          isAuthenticated={this.state.isAuthenticated}
          currentUser={this.state.currentUser}
          onLogout={this.handleLogout}
        />

        <Content className="app-content">
          <div className="container">
            <Switch>
              <Route
                path="/home"
                render={props => (
                  <Home
                    isAuthenticated={this.state.isAuthenticated}
                    currentUser={this.state.currentUser}
                    {...props}
                  />
                )}
              />
              {/* <Route path="/hu" exact={true} component={Home} /> */}
              <Route path="/createSlam" exact={true} component={CreateSlam} />
              <Route path="/slam/:id" component={OpenSlam} />
              <Route path="/left" component={LeftSideComponent} />
            </Switch>

            <Switch>
              {/* <Route exact path="/" 
                  render={(props) => <PollList isAuthenticated={this.state.isAuthenticated} 
                      currentUser={this.state.currentUser} handleLogout={this.handleLogout} {...props} />}>
                </Route> */}
              <Route
                path="/login"
                render={props => (
                  <Login onLogin={this.handleLogin} {...props} />
                )}
              />
              <Route path="/signup" component={Signup} />
              <Route
                path="/users/:username"
                render={props => (
                  <Profile
                    isAuthenticated={this.state.isAuthenticated}
                    currentUser={this.state.currentUser}
                    {...props}
                  />
                )}
              />
              <PrivateRoute
                authenticated={this.state.isAuthenticated}
                path="/poll/new"
                component={signup}
                handleLogout={this.handleLogout}
              />
              {/* <Route component={NotFound} /> */}
            </Switch>
          </div>
        </Content>
      </Layout>
    );
  }
}

export default withRouter(App);
