import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Button, Container, Table } from "reactstrap";
import { getSlams } from "../util/APIUtils";
import { getCurrentUser } from "../util/APIUtils";

class Home extends Component {
  _isMounted = false;
  _isMounted1 = false;
  constructor(props) {
    super(props);
    this.state = {
      slams: []
    };
    this.checking = this.checking.bind(this);
  }
  loadCurrentUser() {
    this.setState({
      isLoading: true
    });
    getCurrentUser()
      .then(response => {
        if (this._isMounted1) {
          this.setState({
            currentUser: response,
            isAuthenticated: true,
            isLoading: false
          });
        }
      })
      .catch(error => {
        if (this._isMounted1) {
          this.props.history.push("/login");
          this.setState({
            isLoading: false
          });
        }
      });
  }
  checking() {
    getSlams().then(data => {
      if (this._isMounted) {
        this.setState({ slams: data });
      }
    });
  }
  componentDidMount() {
    this._isMounted1 = true;
    this.loadCurrentUser();
    this._isMounted = true;

    getSlams().then(data => {
      if (this._isMounted) {
        this.setState({ slams: data });
      }
    });
  }
  componentWillUnmount() {
    this._isMounted = false;
    this._isMounted1 = false;
  }
  render() {
    const { slams } = this.state;
    if (!this._isMounted1) {
      return <p>Loading...</p>;
    }

    if (!this._isMounted) {
      return <p>Loading...</p>;
    }
    return (
      <div className="App">
        <p className="bg-primary">Welcome!! this is home page</p>
        {console.log(this.props.isAuthenticated)}
        <Button color="success" tag={Link} to="/createSlam">
          create new slam
        </Button>
        <Container>
          <Table className="mt-4">
            <thead>
              <tr>
                <th>name</th>
                <th>email</th>
                <th />
              </tr>
            </thead>
            <tbody>
              {slams.map(slam => {
                return (
                  <tr key={slam.id}>
                    <td>{slam.name}</td>
                    <td>{slam.emailid}</td>
                    <td>
                      <Button
                        color="success"
                        tag={Link}
                        to={"/slam/" + slam.id}
                      >
                        open slam
                      </Button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </Table>
        </Container>
      </div>
    );
  }
}

export default Home;
