import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.css";
//import { Form, Button } from "reactstrap";
//import { Form, Icon, Input, Button, Checkbox } from "antd";
import { Link } from "react-router-dom";
import { ACCESS_TOKEN } from "../constants";
import { getCurrentUser } from "../util/APIUtils";
import { login } from "../util/APIUtils";
import { Form, FormGroup, Label, Input, Container, Button } from "reactstrap";

import addNotification from "./notfication";
class Formtesting extends Component {
  emptyItem = {
    usernameOrEmail: "",
    password: ""
  };
  constructor(props) {
    super(props);
    this.state = {
      item: this.emptyItem
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.notificationDOMRef = React.createRef();
  }

  handleSubmit(event) {
    event.preventDefault();
    const { item } = this.state;
    const loginRequest = Object.assign({}, item);
    login(loginRequest)
      .then(response => {
        localStorage.setItem(ACCESS_TOKEN, response.accessToken);
        this.props.onLogin();
      })
      .catch(error => {
        if (error.status === 401) {
          console.log("this is modified error", error);
        } else {
          console.log("this is modified error", error);
        }
      });
  }
  // this.props.form.validateFields((err, values) => {
  //   console.log(values);
  //   if (!err) {
  //     const loginRequest = Object.assign({}, values);
  //     login(loginRequest).then(response => {
  //       localStorage.setItem(ACCESS_TOKEN, response.accessToken);
  //       this.props.onLogin();
  //     });
  // .catch(error => {
  //   if (error.status === 401) {
  //     notification.error({
  //       message: "Polling App",
  //       description:
  //         "Your Username or Password is incorrect. Please try again!"
  //     });
  //   } else {
  //     notification.error({
  //       message: "Polling App",
  //       description:
  //         error.message ||
  //         "Sorry! Something went wrong. Please try again!"
  //     });
  //   }
  // });
  //     }
  //   });
  // }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    let item = { ...this.state.item };
    item[name] = value;
    this.setState({ item });
  }

  render() {
    return (
      <div>
        <p>this is create slam page</p>

        <Container>
          <Form onSubmit={this.handleSubmit}>
            <FormGroup>
              <Label>email:</Label>
              <Input
                type="email"
                name="usernameOrEmail"
                id="usernameOrEmail"
                onChange={this.handleChange}
                required={true}
                message="please gyyvsvs"
              />
            </FormGroup>
            <FormGroup>
              <Label>name:</Label>
              <Input
                type="password"
                name="password"
                id="password"
                onChange={this.handleChange}
                required={true}
              />
            </FormGroup>

            <Button type="submit">Submit</Button>
          </Form>
        </Container>
      </div>
    );
  }
}

export default Formtesting;
